@js:
var tmp, sid, id;
var text = '';
var D = decodeURIComponent;
var alias = {
  'tophub.today': 'tophub-today',
  'daily.zhihu': 'zhihu-daily',
  hjenglish: 'hujiang-en'
};

if (baseUrl.includes('_id')) {
  sid = baseUrl.match(/\?_id=([^=&]+)=?/)[1];
  id = baseUrl.match(/[^_]id=([^=&]+)=?/)[1];
} else {
  for (var key in alias) {
    if (baseUrl.includes(key)) {
      sid = alias[key];
      break;
    }
  }
}

switch (sid) {
  case 'bilibili-zhuanlan': {
    tmp = java.ajax('https://www.bilibili.com/read/cv' + id);
    java.setContent(tmp);
    text = java.getString('class.article-holder@html');
    break;
  }
  case 'zhihu-daily': {
    text = java.getString('class.content@html');
    break;
  }
  case 'hujiang-en': {
    text = String(java.getString('class.article-content@html')).replace(
      /（翻译.+）|图片来源.+/g,
      ''
    );
    break;
  }
  case 'tophub-today': {
    text = String(java.getString('class.Zd-p-Sc@html'))
      .replace(
        //g,
        '\n.\n                      - - - - - - - - - - - - - - - -\n.\n'
      )
      .replace(/网页端只展示.+/, '');
    break;
  }
  case 'source': {
    function reString(str, spliter) {
      return str
        .split(spliter || '')
        .reverse()
        .join('');
    }

    function R(a, b) {
      return (
        (a = Math.ceil(a)),
        (b = Math.floor(b)),
        Math.floor(Math.random() * (b - a + 1)) + a
      );
    }

    function isLuck() {
      return l === r;
    }

    var ecruosUrl = reString(
      '/vuvdvevuvyv/vrvevtvsvavmv/vwvavrv/vevcvrvuvovsv/vsvovuvrvcvev/vmvovcv.vevevtvivgv/v/v:vsvpvtvtvh',
      String.fromCharCode(118)
    );

    text = java.ajax(ecruosUrl + 'README.md');
    text = String(text)
      .replace(/- /g, '∙ ')
      .replace(/`/g, '')
      .replace(/# 百源/, '')
      .replace(/### /g, '▪ ')
      .replace(/## /g, '👉 ');

    var S = String.fromCharCode;
    var l = R(parseInt(S(48)), parseInt(S(56)));
    var r = R(parseInt(S(49)), parseInt(S(57)));
    var h = String(parseInt(Date.now() / 1000 / 60 / 60));
    var sign = l + h + reString(h) + r;
    var q = '18281818985878286';

    var v = baseUrl.match(/version=([^=&]+)/);
    if (v) {
      var key = baseUrl.match(/key=([^=&]+)/)[1];
      v = v[1];

      text =
        '网络导入 v' +
        v +
        '：\n' +
        ecruosUrl +
        'source/' +
        key +
        '.json\n---\n右上的“拷贝内容”比直接长按复制要好用哦！\n> 如果看不到最上面的更新链接，可以在右上对本源禁用“替换净化”。\n---' +
        text;
    } else if (isLuck()) {
      text =
        D('%E3%80%90%E7%BE%A4%E5%8F%B7%E3%80%91') +
        reString(q, '8') +
        '\n' +
        D('%E3%80%90%E6%9A%97%E5%8F%B7%E3%80%91') +
        sign +
        '\n' +
        text;
    }
  }
}

if (!text) {
  text = '´◔◡◔`' + (sid ? '\n' + sid : '');
}

text;

